/**
 * Gets the stringified version of all event handlers on an element
 * @return object
 */
$.fn.events = function() {

    var events = {},
        el_events;

    this.each(function(index, el) {

        el_events = $._data(el[0], 'events');

        $.each(el_events, function(event_type, handlers) {
            events[event_type] = events[event_type] || [];

            for (var i = 0; i < handlers.length; i++)
            {
                events[event_type].push( handlers[i].handler.toString() );
            }
        });

    });
    return events;
};


  function bindProductRollovers(wrapper, single, single_hover, id_prefix, single_hover_prefix)
  {
    // Because these captions are all positioned to the bottom of the products, slideUp causes them to slide down (and hide),
    // slideDown causes them to slide up (which shows them). Fun times!
    var $middle_column = $(wrapper),
      product_hovers = [];
    function store_heights()
    {
      $middle_column.find(single_hover).each(function(){
        var $this = $(this);
        if ( ! $this.data('height'))
          $this.show().data('height', $this.css('height'));
      }).animate({ height: 0 }, 1, function(){ $(this).hide(); });
    }
    store_heights();
    $middle_column.ajaxComplete(store_heights);
    $middle_column.delegate(single, 'mouseover mouseout', function(e){
      var parent_product_id = e.currentTarget.id.replace(id_prefix, '');
      if (e.type === 'mouseover')
      {
        if (product_hovers[parent_product_id])
          clearTimeout(product_hovers[parent_product_id]);
        showCaption(single_hover_prefix + parent_product_id);
      }
      else
      {
        product_hovers[parent_product_id] = setTimeout(function(){
          hideCaption(single_hover_prefix +  parent_product_id);
        }, 250);
      }
    });

    function showCaption(single)
    {
      var $caption = $(single);
      $caption.stop().show().animate({ height: $caption.data('height') }, { easing: 'swing' });
    }
    function hideCaption(single)
    {
      var $caption = $(single);
      $caption.stop().animate({ height: 0 }, { easing: 'swing', complete: function(){$(this).hide()} });
    }
  }

  function bindRelatedProductsRollovers()
  {
    bindProductRollovers('#related_products', 'li', 'div.product_hover', 'related_li_', '#related_product_details_');
  }

  function bindProductListingRollovers()
  {
    bindProductRollovers('#middle_column', 'div.product', 'div.product_hover', 'product_', '#product_hover_');
  }

  function activateSlideShow(product_id)
  {
    if(this['product_slideshow_' + product_id + '_images'] != null && this['product_slideshow_' + product_id + '_images'].length < 2) return;
    $('#product_ss_' + product_id + '_left,#product_ss_' + product_id + '_right').show();
    $('#product_ss_' + product_id).attr('src', this['product_slideshow_' + product_id + '_images'][this['product_slideshow_' + product_id + '_index']]);
  }

  function deactivateSlideShow(product_id, original)
  {
    $('#product_ss_' + product_id+'_left,#product_ss_' + product_id + '_right').hide();
    $('#product_ss_' + product_id).attr('src', original);
    this['product_slideshow_' + product_id + '_index'] = 1;
  }

  function previousSlideShowImage(product_id)
  {
    if(this['product_slideshow_' + product_id + '_index'] == 0)
    {
      this['product_slideshow_' + product_id + '_index'] = (this['product_slideshow_' + product_id + '_images'].length-1);
    }
    else
    {
      this['product_slideshow_' + product_id + '_index']--;
    }
    $('#product_ss_' + product_id).attr('src', this['product_slideshow_' + product_id + '_images'][this['product_slideshow_' + product_id + '_index']]);
  }

  function nextSlideShowImage(product_id)
  {
    if(this['product_slideshow_' + product_id + '_index'] == (this['product_slideshow_' + product_id + '_images'].length-1))
    {
      this['product_slideshow_' + product_id + '_index'] = 0;
    }
    else
    {
      this['product_slideshow_' + product_id + '_index']++;
    }
    $('#product_ss_' + product_id).attr('src', this['product_slideshow_' + product_id + '_images'][this['product_slideshow_' + product_id + '_index']]);
  }

