function find_highest_price_break(element_id_prefix, requested_qty) {
  var highest_price_break_element = 0;
  $('.price_break[id^=' + element_id_prefix + ']').each(function(i,e){
    bits = $(e).prop('id').split('_');
    var number = bits[bits.length-1];
    if (!isNaN(number)) {
      number = parseInt(number);
      if (number > highest_price_break_element && number <= requested_qty) {
        highest_price_break_element = bits[bits.length-1];
      }
    }
  });
  if (highest_price_break_element == 0 || highest_price_break_element > requested_qty) {
    highest_price_break_element = requested_qty;
  }
  return highest_price_break_element;
}
